import React from "react";
import { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import { Grid, Card, CardHeader, TextField, CardContent } from "@mui/material";

function CreateAnime() {
  const [values, setValues] = useState({
    title: "",
    episodesWatched: "",
    episodeCount: "",
    score: "",
    synopsis: "",
    image: "",
    type: "",
    status: "",
    studio: "",
  });
  const [searchAnimeName, setSearchAnimeName] = useState("");

  const API_URL = "https://api.jikan.moe/v4";
  async function searchAnime(search) {
    const response = await fetch(`${API_URL}/anime?q=${search}`);
    const animeData = await response.json();
    console.log(animeData.data[0]);
    setValues({
      ...values,
      title: animeData.data[0].title_english,
      episodeCount: animeData.data[0].episodes,
      synopsis: animeData.data[0].synopsis,
      image: animeData.data[0].images.jpg.image_url,
      type: animeData.data[0].type,
      studio: animeData.data[0].studios[0].name,
    });
  }

  const navigate = useNavigate();

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchAnimeName === "") {
    } else {
      searchAnime(searchAnimeName);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:8081/anime", values)
      .then((res) => {
        console.log(res);
        navigate("/animeList");
      })
      .catch((err) => console.log(err));
  };
  return (
    <Grid container justify="center">
      <Grid item md={3}>
        <Card>
          <CardHeader title="Add Anime"></CardHeader>
          <CardContent>
            <Grid item container justify="center">
              <Grid item>
                <form onSubmit={handleSearchSubmit}>
                  <div className="mb-2">
                    <TextField
                      id="outlined-basic"
                      onChange={(e) => setSearchAnimeName(e.target.value)}
                    ></TextField>
                  </div>
                  <button className="btn btn-success"> Search Anime</button>
                </form>
              </Grid>
            </Grid>
          </CardContent>
        </Card>
      </Grid>
      <Grid item md={6}>
        <Card>
          <CardHeader title="Add Anime"></CardHeader>
          <CardContent>
            <Grid item container justify="center">
              <Grid item>
                <form onSubmit={handleSearchSubmit}>
                  <div className="mb-2">
                    <TextField
                      id="outlined-basic"
                      onChange={(e) => setSearchAnimeName(e.target.value)}
                    ></TextField>
                  </div>
                  <button className="btn btn-success"> Search Anime</button>
                </form>
              </Grid>
            </Grid>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
}

export default CreateAnime;
