import React, { useState, useEffect } from 'react';

function AnimeList() {
  const [anime, setAnime] = useState([]);
  const [newAnimeName, setNewBookName] = useState('');
  const [newEpsiodeCount, setEpisodeCount] = useState('');
  const [editIndex, setEditIndex] = useState(null);

  useEffect(() => {
    const storedAnime = JSON.parse(localStorage.getItem('anime'));
    if (storedAnime) {
      setAnime(storeAnime);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('anime', JSON.stringify(anime));
  }, [anime]);

  const handleAddBook = () => {
    if (newAnimeName && newEpsiodeCount) {
      if (editIndex === null) {
        setBooks([...anime, { name: newBookName, author: newBookAuthor }]);
      } else {
        const updatedBooks = [...books];
        updatedBooks[editIndex] = { name: newBookName, author: newBookAuthor };
        setBooks(updatedBooks);
        setEditIndex(null);
      }
      setNewBookName('');
      setNewBookAuthor('');
    }
  };

  const handleDeleteBook = (index) => {
    const updatedBooks = [...books];
    updatedBooks.splice(index, 1);
    setBooks(updatedBooks);
  };

  const handleEditBook = (index) => {
    const bookToEdit = books[index];
    setNewBookName(bookToEdit.name);
    setNewBookAuthor(bookToEdit.author);
    setEditIndex(index);
  };

  return (
    <div>
      <h1>Book CRUD Application</h1>
      <div>
        <input
          type="text"
          placeholder="Enter Book Name"
          value={newBookName}
          onChange={(e) => setNewBookName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Enter Author Name"
          value={newBookAuthor}
          onChange={(e) => setNewBookAuthor(e.target.value)}
        />
        <button onClick={handleAddBook}>{editIndex !== null ? 'Update Book' : 'Add Book'}</button>
      </div>
      <ul>
        {books.map((book, index) => (
          <li key={index}>
            <span>{book.name} - {book.author}</span>
            <button onClick={() => handleEditBook(index)}>Edit</button>
            <button onClick={() => handleDeleteBook(index)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AnimeList;
